INSERT INTO faculties (name, short_name, description, is_active, date_create, date_update) VALUES ('Социально-гуманитарный факультет', 'СГФ', 'Мы готовим учителей истории, обществознания, права, экономики и специалистов по социальной работе с правом выполнения управленческой и предпринимательской деятельности, специалистов в области психолого-педагогического сопровождения (медиаторов, психологов-консультантов, социальных педагогов).', true, now(), now()),
                                                                                                  ('Факультет филологии и межкультурной коммуникации', 'ФФМК', 'Преподаватели и студенты факультета выполняют научные исследования в смежных областях языкознания, филологии, педагогики и методики.', true, now(), now()),
                                                                                                  ('Факультет психолого-педагогического образования', 'ФППО', 'Факультет психолого-педагогического образования преобразован из института психолого-педагогического образования 1 июля 2013 года. Институт был создан 1 июля 2005 года на базе факультета педагогики и методики начального образования, просуществовавшего 20 лет.', true, now(), now()),
                                                                                                  ('Факультет естествознания, математики и информатики', 'ФЕМИ', 'Факультет естествознания, математики и информатики создан в 2011 г. на основе двух старейших факультетов – физико-математического (основан в 1939 г.) и химико-биологического (основан в 1948 г.).', true, now(), now()),
                                                                                                  ('Факультет художественного образования', 'ФХО', 'Факультет художественного образования Нижнетагильского государственного социально-педагогического института (ф) РГППУ, преемник художественно-графического факультета НТГПИ-НТГСПА-НТГСПИ в 2019 г. отметил свое шестидесятилетие.', true, now(), now()),
                                                                                                  ('Факультет спорта и безопасности жизнедеятельности', 'ФСБЖ', 'Факультет безопасности жизнедеятельности был основан как самостоятельное подразделение на основании приказа Министерства образования РФ № 956 от 12.03.2001г., переименован в факультет физической культуры и безопасности жизнедеятельности в 2010 году, переименован в факультет спорта и безопасности жизнедеятельности в 2011 году.', true, now(), now());
